    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        if(jTextField1.getText().equals("")) {
            JOptionPane.showMessageDialog(rootPane, "Please enter a valid username", "Invalide username", JOptionPane.ERROR_MESSAGE);
            return;
        }
        String password=new String(jPasswordField1.getPassword());
        password=password.toString();
        //JDBC CODE Password Validate Query
        //If invalid show error
        JDBConnection jdbc=new JDBConnection();
        jdbc.connect();
        if(jdbc.checkPassword(password))
        {
            jdbc.changeUsername(jTextField1.getText());
        }
        else
        {
            JOptionPane.showMessageDialog(rootPane, "Please enter the valid password", "Invalide password", JOptionPane.ERROR_MESSAGE);
            return;
        }
        //JDBC CODE Password Update Query
        //If successful show message        
        
        jButton2ActionPerformed(null);
    }//GEN-LAST:event_jButton1ActionPerformed

